#!/bin/bashset -eVariables — change before runningREPO_DIR="GitCleanupPushDemo"REMOTE_URL="https://github.com/YourUsername/GitCleanupPushDemo.git"  # Replace with your GitHub repo URLUSER_NAME="Your Name"USER_EMAIL="you@example.com"Step 1: Prepare directory (if not already cloned)if [ ! -d "$REPO_DIR" ]; thengit clone "$REMOTE_URL" "$REPO_DIR"ficd "$REPO_DIR"Step 2: Configure user detailsgit config user.name "$USER_NAME"git config user.email "$USER_EMAIL"Step 3: Verify if master/main is cleanecho "🔍 Checking if working tree is clean..."git statusStep 4: List all available branchesecho "📋 Branches available:"git branch -aStep 5: Checkout master/main branchif git show-ref --verify --quiet refs/heads/main; thengit checkout mainelsegit checkout masterfiStep 6: Pull latest changes from remoteecho "⬇ Pulling latest changes from remote..."git pull origin $(git branch --show-current)Step 7: Simulate pending changes (for lab demo)echo "This is a pending change from Git-T03-HOL_002 lab." >> pending_changes.txtgit add pending_changes.txtgit commit -m "Add pending_changes.txt for Git cleanup and push lab"Step 8: Push changes back to remoteecho "⬆ Pushing changes to remote..."git push origin $(git branch --show-current)Step 9: Confirm changesecho "✅ Changes have been pushed to $REMOTE_URL"